module.exports = {

   err500 : function (err, res) {
        res.writeHead(500, {
            "Content-type": "text/html"
        });
        res.write('Internal Server error ' + err);
        res.end();
    },


   err404 : function (res) {
        res.writeHead(404, {
            "Content-type": "text/html"
        });
        res.write('Page does not exist!!');
        res.end();
    }

}
